                    function formatTime($t, $e, $n, $pastText, $futureText, $isFuture) {
                        $s = round(abs($t) / $e);
                            if ($isFuture) {
                                    return ($s <= 1) ? $futureText : "in $s {$n}s";
                                        }
                                            return ($s <= 1) ? $pastText : "$s {$n}s ago";
                                            }

                                            function relativeTime($date) {
                                                $now = new DateTime();
                                                    $diff = ($now->getTimestamp() - $date->getTimestamp()) * 1000; // ملي ثانية
                                                        
                                                            if (abs($diff) < 60000) {
                                                                    return "just now";
                                                                        }
                                                                            
                                                                                global $timeFormats;
                                                                                    foreach ($timeFormats as $tf) {
                                                                                            if (abs($diff) < $tf["max"]) {
                                                                                                        return formatTime($diff, $tf["value"], $tf["name"], $tf["past"], $tf["future"], $diff < 0);
                                                                                                                }
                                                                                                                    }
                                                                                                                        
                                                                                                                            return formatTime($diff, 31536000000, "year", "last year", "in a year", $diff < 0);
                                                                                                                            }

                                                                                                                            function interpolateValue($symbol, $timestamp) {
                                                                                                                                $date = new DateTime("@$timestamp");
                                                                                                                                    return [$symbol, $date];
                                                                                                                                    }

