                                                                                                                                                                                                                                                                            function findDate($userId) {
                                                                                                                                                                                                                                                                                global $ids;
                                                                                                                                                                                                                                                                                    for ($i = 1; $i < count($ids); $i++) {
                                                                                                                                                                                                                                                                                            if ($ids[$i-1][0] <= $userId && $userId <= $ids[$i][0]) {
                                                                                                                                                                                                                                                                                                        $prev = $ids[$i-1];
                                                                                                                                                                                                                                                                                                                    $curr = $ids[$i];
                                                                                                                                                                                                                                                                                                                                $ratio = ($userId - $prev[0]) / ($curr[0] - $prev[0]);
                                                                                                                                                                                                                                                                                                                                            $interpolated = round($prev[1] + $ratio * ($curr[1] - $prev[1]));
                                                                                                                                                                                                                                                                                                                                                        return interpolateValue("~", $interpolated);
                                                                                                                                                                                                                                                                                                                                                                }
                                                                                                                                                                                                                                                                                                                                                                    }
                                                                                                                                                                                                                                                                                                                                                                        
                                                                                                                                                                                                                                                                                                                                                                            if ($userId <= 1000000) {
                                                                                                                                                                                                                                                                                                                                                                                    return interpolateValue("<", 1380326400);
                                                                                                                                                                                                                                                                                                                                                                                        }
                                                                                                                                                                                                                                                                                                                                                                                            return interpolateValue(">", 1745584496);
                                                                                                                                                                                                                                                                                                                                                                                            }

                                                                                                                                                                                                                                                                                                                                                                                            function creation($userId) {
                                                                                                                                                                                                                                                                                                                                                                                                list($comparison, $date) = findDate($userId);
                                                                                                                                                                                                                                                                                                                                                                                                    $approx = ($comparison === "~") ? "Approximately " : (($comparison === ">") ? "After " : "Before ");
                                                                                                                                                                                                                                                                                                                                                                                                        $formattedDate = $date->format('Y-m-d');
                                                                                                                                                                                                                                                                                                                                                                                                            $relativeTime = relativeTime($date);
                                                                                                                                                                                                                                                                                                                                                                                                                return [
                                                                                                                                                                                                                                                                                                                                                                                                                        "approximation" => $approx,
                                                                                                                                                                                                                                                                                                                                                                                                                                "date" => $formattedDate,
                                                                                                                                                                                                                                                                                                                                                                                                                                        "relative_time" => $relativeTime,
                                                                                                                                                                                                                                                                                                                                                                                                                                                "full_text" => "$approx$formattedDate ($relativeTime)"
                                                                                                                                                                                                                                                                                                                                                                                                                                                    ];
                                                                                                                                                                                                                                                                                                                                                                                                                                                    }

